'use strict';

module.exports = function(env) {

  var subset = function(s, k, a, df, field, value) {
    return k(s, _.filter(df, function(d) { return d[field] === value }));
  };

  var trueSentences = function(s, k, a, df) {
    return k(s, _.filter(df, function(d) { return d[1]}));
  };


  return { subset: subset, 
 		 	trueSentences: trueSentences};

};